/* eslint-disable react-hooks/exhaustive-deps */
import { useEffect, useState } from "react";
import { SignClient } from "@walletconnect/sign-client";
import './App.css';

function App() {
  const [signClient, setSignClient] = useState();

  async function createClient() {
    try {
      const client = await SignClient.init({
        projectId: process.env.REACT_APP_PROJECT_ID,
      })
      console.log(client);
      setSignClient(client);
    } catch (e) {
      console.log(e);
    }
  }

  async function handleconnect() {
    if (!signClient) throw Error("cannot connect, signClient is not defined")
    try {
      // wotkflow of the dapp to connect to the wallet
      // 1. dapp is going to send a proposal namespace ( asking for permission to do things with the wallet and listen to their event)
      const proposalNamespace = {
        eip155: {
          chainId: ["@walletconnect/sign-client:5"],
          methods: ["eth_sendTransaction"],
          events: ["connect", "disconnect"]
        }
      };

      const { uri } = await signClient.connect({
        requiredNamespaces: proposalNamespace
      });

      console.log('uri', uri);

      // 2.
    } catch (e) {
      console.log(e);
    }
  }
  useEffect(() => {
    if (!signClient) {
      createClient();
    }
  }, [signClient]);

  return (
    <div className="App">
      <h1> INTU-nnexion </h1>
      <button onClick={handleconnect} disable={!signClient}> Connect </button>
    </div>
  );
}

export default App;
